INSERT INTO `db_config` VALUES (1, 'site_title', 'Tunifact - تونس تتحرى');
INSERT INTO `db_config` VALUES (2, 'site_description', 'موقع متخصص في صحافة التحري يتبع لنقابة الصحفيين التونسيين');
