INSERT INTO `db_config` VALUES (4, 'site_statut', '1');
INSERT INTO `db_config` VALUES (15, 'about_txt', '<p>The&nbsp;African Economic and&nbsp;Monetary Sovereignty Sovereignty initiative began almost three years ago in preparation for the first conference on&nbsp;<a href="https://rosaluxna.org/projects/monetary-sovereignty-conference/" data-saferedirecturl="https://www.google.com/url?q=https://rosaluxna.org/projects/monetary-sovereignty-conference/&amp;source=gmail&amp;ust=1599344427191000&amp;usg=AFQjCNGvWT7Rg2YwWeOh3aTvOzS3MSFhow">The Quest for Economic and Monetary Sovereignty&nbsp;in 21st Century&nbsp;Africa</a>, which was held in Tunis (November 6-9, 2019). An edited volume from the conference will be published by Pluto Press (2021), and a second edition of the conference will take place in Dakar in 2021 (post-pandemic hopefully!). Many of the participants at the Tunis conference urged us to continue building on the conference momentum, and to reach beyond academic and government circles and into civil society movements.&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>Since the pandemic began, we''ve seen several declarations about the&nbsp;pandemic&nbsp;effects in Africa, most notably this one from&nbsp;<a href="https://darajapress.com/2020/04/30/declaration-of-african-intellectuals-concerning-covid-19-crisis" data-saferedirecturl="https://www.google.com/url?q=https://darajapress.com/2020/04/30/declaration-of-african-intellectuals-concerning-covid-19-crisis&amp;source=gmail&amp;ust=1599344427191000&amp;usg=AFQjCNE8Tid76UQPSI4LKn2C2AIiaiNMxQ">African public intellectuals</a>&nbsp;and and this one from&nbsp;<a href="https://africanfeminism.com/african-feminist-post-covid-19-economic-recovery-statement/" data-saferedirecturl="https://www.google.com/url?q=https://africanfeminism.com/african-feminist-post-covid-19-economic-recovery-statement/&amp;source=gmail&amp;ust=1599344427191000&amp;usg=AFQjCNHGJ7TJW049-L0CrhGGa5xJ0xtNtg">African Feminists</a>&nbsp;calling for deep structural reforms to build more resilience during the pandemic. We wanted to add our voices to this momentum by highlighting the importance of enhancing economic and monetary sovereignty in Africa and implementing an alternative&nbsp;economic development model.&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>While communicating these ideas to African leaders is important, we believe&nbsp;it is critical that we engage with grassroots movements, NGO leaders, activists, labor organizers, and average citizens, which is why we provided several translations in African languages and other languages&nbsp;from the Global South. We wanted to speak directly to the average person in their own language without the filters of government institutions and mainstream media. We are hoping that this letter will spark a broader public discourse about an alternative economic development policy framework, and that it will&nbsp;provide a coherent anti-austerity narrative for the Global South. We also decided to include several audio readings of this open letter in multiple&nbsp;languages in a genuine effort to reach people who cannot read, people who are&nbsp;visually&nbsp;impaired, and because many native/ancient languages and cultures rely primarily on oral communication.</p>\r\n<p><strong>Available Translations</strong>: Afrikaans, Arabic, Bengali, Burmese, Catalan, Chichewa, Chinese (Simplified), Chinese (Traditional), Danish, Dutch, Fijian, Finnish, French, Fulani, German, Greek, Hausa, Hindi, Icelandic, Igbo, Indonesian, IsiXhosa, IsiZulu, Italian, Japanese, Kipsigis, Korean, Malagasy, Malay, Marathi, Maya, Northern Sotho, Norwegian, Polish, Portuguese, Russian, Southern Sotho, Slovak, Spanish, Tamil, Telugu, Thai, Turkish, Ukrainian, Urdu, Vietnamese, Wolof, Yoruba.</p>');
INSERT INTO `db_config` VALUES (21, 'homepage_txt', '<p>The&nbsp;African Economic and&nbsp;Monetary Sovereignty Sovereignty initiative began almost three years ago in preparation for the first conference on&nbsp;<a href="https://rosaluxna.org/projects/monetary-sovereignty-conference/" data-saferedirecturl="https://www.google.com/url?q=https://rosaluxna.org/projects/monetary-sovereignty-conference/&amp;source=gmail&amp;ust=1599344427191000&amp;usg=AFQjCNGvWT7Rg2YwWeOh3aTvOzS3MSFhow">The Quest for Economic and Monetary Sovereignty&nbsp;in 21st Century&nbsp;Africa</a>, which was held in Tunis (November 6-9, 2019). An edited volume from the conference will be published by Pluto Press (2021), and a second edition of the conference will take place in Dakar in 2021 (post-pandemic hopefully!). Many of the participants at the Tunis conference urged us to continue building on the conference momentum, and to reach beyond academic and government circles and into civil society movements.&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>Since the pandemic began, we''ve seen several declarations about the&nbsp;pandemic&nbsp;effects in Africa, most notably this one from&nbsp;<a href="https://darajapress.com/2020/04/30/declaration-of-african-intellectuals-concerning-covid-19-crisis" data-saferedirecturl="https://www.google.com/url?q=https://darajapress.com/2020/04/30/declaration-of-african-intellectuals-concerning-covid-19-crisis&amp;source=gmail&amp;ust=1599344427191000&amp;usg=AFQjCNE8Tid76UQPSI4LKn2C2AIiaiNMxQ">African public intellectuals</a>&nbsp;and and this one from&nbsp;<a href="https://africanfeminism.com/african-feminist-post-covid-19-economic-recovery-statement/" data-saferedirecturl="https://www.google.com/url?q=https://africanfeminism.com/african-feminist-post-covid-19-economic-recovery-statement/&amp;source=gmail&amp;ust=1599344427191000&amp;usg=AFQjCNHGJ7TJW049-L0CrhGGa5xJ0xtNtg">African Feminists</a>&nbsp;calling for deep structural reforms to build more resilience during the pandemic. We wanted to add our voices to this momentum by highlighting the importance of enhancing economic and monetary sovereignty in Africa and implementing an alternative&nbsp;economic development model.&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>While communicating these ideas to African leaders is important, we believe&nbsp;it is critical that we engage with grassroots movements, NGO leaders, activists, labor organizers, and average citizens, which is why we provided several translations in African languages and other languages&nbsp;from the Global South. We wanted to speak directly to the average person in their own language without the filters of government institutions and mainstream media. We are hoping that this letter will spark a broader public discourse about an alternative economic development policy framework, and that it will&nbsp;provide a coherent anti-austerity narrative for the Global South. We also decided to include several audio readings of this open letter in multiple&nbsp;languages in a genuine effort to reach people who cannot read, people who are&nbsp;visually&nbsp;impaired, and because many native/ancient languages and cultures rely primarily on oral communication.</p>\r\n<p><strong>Available Translations</strong>: Afrikaans, Arabic, Bengali, Burmese, Catalan, Chichewa, Chinese (Simplified), Chinese (Traditional), Danish, Dutch, Fijian, Finnish, French, Fulani, German, Greek, Hausa, Hindi, Icelandic, Igbo, Indonesian, IsiXhosa, IsiZulu, Italian, Japanese, Kipsigis, Korean, Malagasy, Malay, Marathi, Maya, Northern Sotho, Norwegian, Polish, Portuguese, Russian, Southern Sotho, Slovak, Spanish, Tamil, Telugu, Thai, Turkish, Ukrainian, Urdu, Vietnamese, Wolof, Yoruba.</p>');
