CREATE TABLE `db_lessons_pass` (
  `id` int(10) unsigned NOT NULL auto_increment,
  `userid` int(11) default NULL,
  `lesson_id` int(11) default NULL,
  `next_lesson_id` int(11) default NULL,
  `last_lesson_id` int(11) default NULL,
  `certif_id` int(11) default NULL,
  `module_id` int(11) default NULL,
  `pass_it` int(11) NOT NULL default '0',
  `year_id` int(11) default NULL,
  `times_of_passit` int(11) default NULL,
  `score_pass` int(11) default NULL,
  `date` int(10) unsigned default NULL,
  PRIMARY KEY  (`id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=4 ;

